property.value:
# pipeline of yarn apps
input {
  exec {
    command => "if [ `hostname` == {{rm_host}} ]; then find {{logstash_log_dir}} -name yarn.apps -size +100M -delete; python {{logstash_bin}}/yarn-apps.py >> {{logstash_log_dir}}/yarn.apps; fi"
    interval => 60
  }
}
input {
  file {
    path => "{{logstash_log_dir}}/yarn.apps"
    sincedb_path => "{{logstash_sincedb_path}}/yarn.apps"
    type => "yarn.apps"
  }
}
filter {
  if [type] == "yarn.apps" {
    grok {
      match => {   
        "message" => "%{GREEDYDATA:appId}\s+%{WORD:appType}\s+%{WORD:appState}\s+%{WORD:finalStatus}\s+%{GREEDYDATA:queue}\s+%{GREEDYDATA:user}\s+%{NUMBER:vCores}\s+%{NUMBER:memory}\s+%{NUMBER:vCoreCapacity}\s+%{NUMBER:memoryCapacity}\s+%{NUMBER:vcoreSeconds}\s+%{NUMBER:memorySeconds}\s+%{NUMBER:elapsedTime}"
      }
    }
    mutate {
      convert => ["vCores", "integer"]
      convert => ["memory", "integer"]
      convert => ["vCoreCapacity", "integer"]
      convert => ["memoryCapacity", "integer"]
      convert => ["vcoreSeconds", "integer"]
      convert => ["memorySeconds", "integer"]
      convert => ["elapsedTime", "integer"]
    }
    if ("_grokparsefailure" in [tags]) { drop {} }
  }
}
output {
  if [type] == "yarn.apps" {
    if [appState] == "RUNNING" {
      elasticsearch {
        hosts => [{% for node in elastic_data_hosts %}"{{node}}:{{elastic_port}}"{% if not loop.last %},{% endif %}{% endfor %}]
        index => "logstash-yarn-running"
        }
    }
    if [appType] != "FREE" {
      elasticsearch {
        hosts => [{% for node in elastic_data_hosts %}"{{node}}:{{elastic_port}}"{% if not loop.last %},{% endif %}{% endfor %}]
        index => "logstash-yarn-apps"
        document_id => "yarn-%{appId}"
        doc_as_upsert => true
        action => "update"
      }
    }
  }
}

# pipeline of disk usage
input {
  exec {
    command => "df --total -l -h|grep total"
    interval => 30
    type => "disk"
  }
}
filter {
  if [type] == "disk" {
    grok {
      match => {   
        "message" => "total\s+%{NUMBER:total}G\s+%{NUMBER:used}G\s+%{NUMBER:avail}G"
      }
    }
    mutate {
      convert => ["total", "integer"]
      convert => ["used", "integer"]
      convert => ["avail", "integer"]
    }
    if ("_grokparsefailure" in [tags]) { drop {} }
  }
}

output {
  if [type] == "disk" {
    elasticsearch {
      hosts => [{% for node in elastic_data_hosts %}"{{node}}:{{elastic_port}}"{% if not loop.last %},{% endif %}{% endfor %}]
      index => "logstash-disk"
    }
  }
}

#pipeline of hdfs namenode logs
input {
  file {
    path => "{{hdfs_log_dir_prefix}}/{{hdfs_user}}/*-namenode-*.log"
    sincedb_path => "{{logstash_sincedb_path}}/hdfs.namenode.log"
    start_position => beginning
    type => "hdfs.namenode.log"
  }
}

filter {
  if [type] == "hdfs.namenode.log" {
    grok {
      match => {   
        "message" => "%{TIMESTAMP_ISO8601:timestamp}\s+%{LOGLEVEL:loglevel}\s+%{GREEDYDATA:content}"
      }
    }
    if [loglevel] != "ERROR" and [loglevel] != "WARN" and [loglevel] != "FATAL" {
      drop {}
    }
  }
}

output {
  if [type] == "hdfs.namenode.log" {
    elasticsearch {
      hosts => [{% for node in elastic_data_hosts %}"{{node}}:{{elastic_port}}"{% if not loop.last %},{% endif %}{% endfor %}]
      index => "logstash-hdfs-namenode-log"
    }
  }
}

